<#
.DESCRIPTION
	Makes a Company Portal shhortcut on the Desktop of all users.
.LINK
	https://github.com/El3ctr1cR/powershell-tools
.NOTES
	Author: Ruben Ruitenberg
#>

$TargetFile =  "C:\Windows\explorer.exe"
$publicDesktopPath = [System.Environment]::GetFolderPath('CommonDesktopDirectory')
$ShortcutFile = "$publicDesktopPath\Company Portal.lnk"
$localIconPath = "$env:TEMP\CompanyPortalApp.ico"
$iconBase64 = @"

"@
[IO.File]::WriteAllBytes($localIconPath, [Convert]::FromBase64String($iconBase64))
$WScriptShell = New-Object -ComObject WScript.Shell
$Shortcut = $WScriptShell.CreateShortcut($ShortcutFile)
$Shortcut.Arguments = "shell:AppsFolder\Microsoft.CompanyPortal_8wekyb3d8bbwe!App"
$Shortcut.IconLocation = "$localIconPath,0"
$Shortcut.TargetPath = $TargetFile
$Shortcut.Save()